import React from 'react'
import axios from "axios";
import { useEffect, useState } from "react";

function Garderoba() {
    const [listOfVrstaOdjece, setListOfVrstaOdjece] = useState([]);
    const [kolicina, setKolicina] = useState(1);

    useEffect(() => {
      axios.get("http://localhost:3100/vrsta_odjece").then((response) => {
        setListOfVrstaOdjece(response.data);
      });
    }, []);

    const handleQuantity = (type) => {
      if (type === "dec") {
        kolicina > 1 && setKolicina(kolicina - 1);
      } else {
        setKolicina(kolicina + 1);
      }
    }

  return (
    <div className="garderoba">
    {listOfVrstaOdjece.map((value, key) => {
        return (
          <div className="post">
            <div className="title">{value.vrstaOdjeceID}</div>
            <div className="body"> <img alt="Slika" src={value.ikonica} width="150" height="150"></img> </div>
            <div className="footer">
            <h1>{value.nazivVrste}</h1>
            </div>
              <div>
                <div classname="add">
                  <button className="btn" onClick={()=> handleQuantity("dec")}> - </button>
                  <h1>{kolicina}</h1>
                  <button className="btn" onClick={()=> handleQuantity("inc")}> + </button>
                </div>
                <button>+</button>
              </div>
          </div>
        );
      })}
   </div>
  )
}

export default Garderoba
