import React from 'react'
import axios from "axios";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import { useEffect, useState } from "react";
import * as Yup from "yup"; 

function Registracija() {

    const [listOfClanarina, setListOfClanarina] = useState([]);

    
    useEffect(() => {
        axios.get("http://localhost:3100/clanarina").then((response) => {
            setListOfClanarina(response.data);
        });
      }, []);
  

    const initialValues = {
        ime: "",
        prezime: "",
        jmbg: "",
        datumRodjenja: "",
        pocetakVolontiranja: "",
        email: "",
        lozinka: "",
        adresaID: "",
        clanarinaID: "",
        uloga: ""
      };

      const validationSchema = Yup.object().shape({
       /* nazivVrste: Yup.string().max(45).required("Polje tip garderobe ne smije da bude prazno!"),
        ikonica: Yup.string().max(150)*/
      })

    return (
        <div className='dodavanjeGarderobe'>
            <Formik /*initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}*/>
                <Form className="formContainer">
                    <label> Ime: </label>
                    <Field autocomplete="off" id="inputCreatePost" name="ime" placeholder="Unesite ime" />
                    <label> Prezime: </label>
                    <Field autocomplete="off" id="inputCreatePost" name="prezime" placeholder="Unesite prezime" />
                    <label> JMBG: </label>
                    <Field autocomplete="off" id="inputCreatePost" name="jmbg" placeholder="Unesite jmbg" />
                    <label> Datum rođenja: </label>
                    <input type="date" id="inputCreatePost"  name="datumRodjenja" />
                    <label> Početak volontiranja: </label>
                    <input  type="date" id="inputCreatePost"  name="pocetakVolontiranja" />
                    <label> Email: </label>
                    <input  autocomplete="off" id="inputCreatePost" name="email" placeholder="Unesite email" />
                    <label> Lozinka: </label>
                    <input  type="password" id="inputCreatePost" name="lozinka" placeholder="Unesite lozinku" />
                    <label> AdresaID: </label>
                    <input  autocomplete="off" id="inputCreatePost" name="adresaId" placeholder="Unesite ID adrese" />
                    <label> Članarina: </label>
                    <select  id="inputCreatePost" name="cars" form="clanarinaform" placeholder="Izaberite tip članarine">
                    {listOfClanarina.map((value, key) => {
                    return (
                        <option value="vrstaClanarine">{value.vrstaClanarine},{value.iznos}</option>
                    );
                })}
                    </select>
                    <button type="submit"> Dodaj </button>
                </Form>
            </Formik>
        </div>
      )
    }
    

export default Registracija
