import React from 'react'
import axios from "axios";
import { useState } from "react";

function Login() {
  const [email, setEmail] = useState("");
  const [lozinka, setLozinka] = useState("");

  const[loginStatus, setLoginStatus] = useState("");



    const login = () => {
      axios.post("http://localhost:3100/login", {
        email: email,
        lozinka: lozinka,
      }).then((response) => {
        if(response.data.message) {
          setLoginStatus(response.data.message);
        } else {
          setLoginStatus(response.data[0].email)
        }
      });
    };

  return (
    <div>
    <div className='login'>
      <div className='formContainer'>
                  <label> Email: </label>
                  <input id="inputCreatePost" type="text" placeholder='Unesite email' onChange={(e)=> {
                    setEmail(e.target.value);
                  }}/>
                  <label> Lozinka: </label>
                  <input id="inputCreatePost" type="text" placeholder='Unesite lozinku' onChange={(e)=> {
                    setLozinka(e.target.value);
                  }}/>                    
                  <button onClick={login}> Login </button>
                  </div>
            </div>
            
      <div className="text"> <h1> {loginStatus} </h1> </div> 
            </div>

    )
  }
  

export default Login
