import React from 'react'
import axios from "axios";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import { useEffect, useState } from "react";
import * as Yup from "yup"; 

function Login() {
  const [listOfClanarina, setListOfClanarina] = useState([]);
  const [listOfGrad, setListOfGrad] = useState([]);

  
  useEffect(() => {
      axios.get("http://localhost:3100/clanarina").then((response) => {
          setListOfClanarina(response.data);
      });
    }, []);

    useEffect(() => {
      axios.get("http://localhost:3100/grad").then((response) => {
          setListOfGrad(response.data);
      });
    }, []);

  const initialValues = {
      email: "",
      lozinka: ""
    };

    const validationSchema = Yup.object().shape({
     /* nazivVrste: Yup.string().max(45).required("Polje tip garderobe ne smije da bude prazno!"),
      ikonica: Yup.string().max(150)*/
    })

    const onSubmit = (data) => {
      console.log(data);
      axios.get("http://localhost:3100/auth/korisnik", (data)).then((response) => {
        console.log("Uspješno dodavanje!");
        console.log(data);
      });
    };

  return (
      <div className='dodavanjeGarderobe'>
          <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
              <Form className="formContainer">
                  <label> Email: </label>
                  <Field autocomplete="off" id="inputCreatePost" name="email" placeholder="Unesite email" />
                  <label> Lozinka: </label>
                  <Field autocomplete="off" id="inputCreatePost" name="lozinka" placeholder="Unesite lozinku" />
                  <button type="submit"> Dodaj </button>
              </Form>
          </Formik>
      </div>
    )
  }
  

export default Login
