import React from 'react'
import axios from "axios";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import { useEffect, useState } from "react";
import * as Yup from "yup"; 

function Login() {
  const [email, setEmail] = useState([]);
  const [lozinka, setLozinka] = useState([]);

  const initialValues = {
      email: "",
      lozinka: ""
    };

    const validationSchema = Yup.object().shape({
     /* nazivVrste: Yup.string().max(45).required("Polje tip garderobe ne smije da bude prazno!"),
      ikonica: Yup.string().max(150)*/
    })

    const login = () => {
      axios.post("http://localhost:3100/login", {
        email: email,
        lozinka: lozinka,
      }).then((response) => {
        console.log(response);
      });
    };

  return (
      <div className='dodavanjeGarderobe'>
          <Formik>
              <Form className="formContainer">
                  <label> Email: </label>
                  <input type="text" placeholder='Unesite email' onChange={(e)=> {
                    setEmail(e.target.value);
                  }}/>
                  <label> Lozinka: </label>
                  <input type="text" placeholder='Unesite lozinku' onChange={(e)=> {
                    setLozinka(e.target.value);
                  }}/>                    
                  <button onClick={login}> Login </button>
              </Form>
          </Formik>
      </div>
    )
  }
  

export default Login
