import React from 'react'
import axios from "axios";
import { useState } from "react";

function Login() {
  const [ime] = useState("");
  const [prezime] = useState("");
  const [email, setEmail] = useState("");
  const [lozinka, setLozinka] = useState("");

  const[loginStatus, setLoginStatus] = useState("");



    const login = () => {
      axios.post("http://localhost:3100/login", {
        ime: ime,
        prezime: prezime,
        email: email,
        lozinka: lozinka,
      }).then((response) => {
        if(response.data.message) {
          setLoginStatus(response.data.message);
        } else {
          setLoginStatus(response.data[0].ime + ' ' + response.data[0].prezime)
        }
      });
    };

  return (
    <div className='login' >
      <div className='formContainer'>
                  <label> Email: </label>
                  <input id="inputCreatePost" type="text" placeholder='Unesite email' onChange={(e)=> {
                    setEmail(e.target.value);
                  }}/>
                  <label> Lozinka: </label>
                  <input id="inputCreatePost" type="text" placeholder='Unesite lozinku' onChange={(e)=> {
                    setLozinka(e.target.value);
                  }}/>                    
                  <button onClick={login}> Login </button>
                  <label> {loginStatus} </label>  
                  </div>
      </div>
    )
  }
  

export default Login
