import React from 'react'
import axios from "axios";
import { useEffect, useState } from "react";

function Garderoba() {
    const [listOfVrstaOdjece, setListOfVrstaOdjece] = useState([]);
    const [kolicina, setKolicina] = useState(1);

    const [clanarina] = React.useState({
      vrstaClanarine: "Redovna",
      iznos: 300
    });

    useEffect(() => {
      axios.get("http://localhost:3100/vrsta_odjece").then((response) => {
        setListOfVrstaOdjece(response.data);
      });
    }, []);

    const handleQuantity = (type) => {
      if (type === "dec") {
        kolicina > 1 && setKolicina(kolicina - 1);
      } else {
        setKolicina(kolicina + 1);
      }
    }

    const handleClick = () => {
      console.log("Ažuriranje kartice");
      //axios.post
    }

    function removeCartItem(event) {
      console.log(event); 
      var shopItem = event.parentElement.parentElement
      var title = shopItem.getElementsByClassName('btn')[0].innerText
      console.log(title); 
  }

  return (
    <div className="garderoba">
    {listOfVrstaOdjece.map((value, key) => {
        return (
          <div className="post" key={key}>
            <div className="title">{value.nazivVrste}</div>
            <div className="body"> <img alt="Slika" src={value.ikonica} width="150" height="150"></img> </div>
            <div className="footer">
                 <div className="title">Količina:</div>
                  <button className="btn" onClick={()=> handleQuantity("dec")}> - </button>
                  <div className="foot">{value.kolicina}</div>
                  <button className="btn" onClick={()=> handleQuantity("inc")}> + </button>
                  <button className="btnadd" onClick={()=> removeCartItem()}>DODAJ</button>
            </div>
          </div>
        );
      })}
   </div>
  )
}

export default Garderoba
