import React from 'react'
import axios from "axios";
import { useEffect, useState } from "react";

function Korisnici() {
    const [listOfKorisnici, setListOfKorisnici] = useState([]);
    const [kolicina, setKolicina] = useState(1);

    useEffect(() => {
      axios.get("http://localhost:3100/korisnik").then((response) => {
        setListOfKorisnici(response.data);
      });
    }, []);

    const handleQuantity = (type) => {
      if (type === "dec") {
        kolicina > 1 && setKolicina(kolicina - 1);
      } else {
        setKolicina(kolicina + 1);
      }
    }

    const handleClick = () => {
      console.log("Ažuriranje kartice");
      //axios.post
    }

  return (
    <div className="kor">
          <div className="korisnici">
            <div className="korisnici">ID</div>
            <div className="korisnici">Ime</div>
            <div className="korisnici">Prezime</div>
            <div className="korisnici">Adresa</div>
            <div className="korisnici">Grad</div>
            <div className="korisnici">Uloga</div>
          </div>
    {listOfKorisnici.map((value, key) => {
        return (
          <div className="korisnici">
            <div className="korisnik">{value.korisnikID}</div>
            <div className="korisnici">{value.ime}</div>
            <div className="korisnici">{value.prezime}</div>
            <div className="korisnici">{value.adresa}</div>
            <div className="korisnici">{value.nazivGrada}</div>
            <div className="korisnici">{value.uloga}</div>
          </div>
        );
      })}
   </div>
  )
}

export default Korisnici
